mapper.update:
update sys_permission

	pName=#{pName},
	initial=#{initial}
	,pType=#{pType},
	icon=#{icon},url=#{url},resource=#{resource},
	permissionStatus=#{permissionStatus},
	sort=#{sort},`describe`=#{describe},
	isFixed=#{isFixed},
	parentId=#{parentId}

where pId=#{pId}

update sys_permission
set permissionStatus=#{newPermissionState}
where pId=#{pId}
