mapper.update:
  UPDATE safetystock set				
	checker=#{checker},checktime=now(),checktag='审核未通过',configStatus='未完成'
	WHERE safetystockId=#{safetystockId}

mapper.select:
select s.id,s.safetystockId,p.product_id as productId,p.product_name as productName,s.configStatus,s.checktag,s.registertime from safetystock s,product p
p.product_name=#{product_name}
and s.id=p.id

select s.id,s.safetystockId,p.product_id as productId,p.product_name as productName,s.configStatus,s.checktag,s.registertime from safetystock s,product p
s.registertime=#{registertime}
and s.id=p.id
